acher`,`login_url_student`,`username`,`password`,`image_name`,`is_active`,`queue`) VALUES  ('"                . $this->full_name . "','"                . $this->nic_number . "','"                . $this->mobile_number . "','"                . $this->email . "','"                . $this->city . "','"                . $this->address . "','"                . $this->subject . "','"                . $this->login_url_teacher . "','"                . $this->login_url_student . "','"                . $this->username . "','"                . $this->password . "','"                . $this->image_name . "', '"                . $this->is_active . "', '"                . $this->queue . "')";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            return TRUE;        } else {            return FALSE;        }    }    public function all() {        $query = "SELECT * FROM `teacher` ORDER BY queue ASC";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysqli_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function getActiveTeachers() {        $query = "SELECT * FROM `teacher` WHERE `is_active` = 1 ORDER BY queue ASC";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysqli_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function getInActiveTeachers() {        $query = "SELECT * FROM `teacher` WHERE `is_active` =0 ORDER BY queue ASC";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysqli_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function update() {        $query = "UPDATE  `teacher` SET "                . "`full_name` ='" . $this->full_name . "', "                . "`nic_number` ='" . $this->nic_number . "', "                . "`mobile_number` ='" . $this->mobile_number . "', "                . "`email` ='" . $this->email . "', "                . "`city` ='" . $this->city . "', "                . "`address` ='" . $this->address . "', "                . "`subject` ='" . $this->subject . "', "                . "`login_url_teacher` ='" . $this->login_url_teacher . "', "                . "`login_url_student` ='" . $this->login_url_student . "', "                . "`username` ='" . $this->username . "', "                 . "`password` ='" . $this->password . "', "                . "`image_name` ='" . $this->image_name . "', "                . "`is_active` ='" . $this->is_active . "', "                . "`queue` ='" . $this->queue . "' "                . "WHERE `id` = '" . $this->id . "'";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            return $this->__construct($this->id);        } else {            return FALSE;        }    }    public function delete() {        $this->deleteDatesPhotos();        $query = 'DELETE FROM `teacher` WHERE id="' . $this->id . '"';        $db = new Database();        return $db->readQuery($query);    }    public function deleteDatesPhotos() {        $TOUR_DATE_PHOTOS = new TourDatePhoto(NULL);        $allPhotos = $TOUR_DATE_PHOTOS->getTourDatePhotosById($this->id);        foreach ($allPhotos as $photo) {            $IMG = $TOUR_DATE_PHOTOS->image_name = $photo["image_name"];            unlink(Helper::getSitePath() . "upload/full_name-package/date/gallery/" . $IMG);            unlink(Helper::getSitePath() . "upload/full_name-package/date/gallery/thumb/" . $IMG);            $TOUR_DATE_PHOTOS->id = $photo["id"];            $TOUR_DATE_PHOTOS->delete();        }    }    public function login($username, $password) {        $enPass = md5($password);        $query = "SELECT `id` FROM `teacher` WHERE `is_active` =1 AND  `username`= '" . $username . "' AND `password`= '" . $enPass . "'";        $db = new Database();        $result = mysqli_fetch_array($db->readQuery($query));        if (!$result) {            return FALSE;        } else {            $this->id = $result['id'];            $this->setAuthToken($result['id']);            $this->setLastLogin($this->id);            $teacher = $this->__construct($this->id);            $this->setUserSession($teacher);            return $teacher;        }    }    public function checkOldPass($id, $password) {        $enPass = md5($password);        $query = "SELECT `id` FROM `student` WHERE `id`= '" . $id . "' AND `password`= '" . $enPass . "'";        $db = new Database();        $result = mysqli_fetch_array($db->readQuery($query));        if (!$result) {            return FALSE;        } else {            return TRUE;        }    }    public function changePassword($id, $password) {        $enPass = md5($password);        $query = "UPDATE  `student` SET "                . "`password` ='" . $enPass . "' "                . "WHERE `id` = '" . $id . "'";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            return TRUE;        } else {            return FALSE;        }    }    public function ChangeProPic($student, $file) {        $query = "UPDATE  `teacher` SET "                . "`image_name` ='" . $file . "' "                . "WHERE `id` = '" . $student . "'";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            return TRUE;        } else {            return FALSE;        }    }    public function checkMobileVerificationCode($code) {        $query = "SELECT * FROM `